import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";
import image from "../assets/fondonegro.webp";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join("-");
  const detailGenres = myVideogame.genres?.join("-");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <DetailContainer>
      <Image src={image} alt="" />
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <ContainerGetInfo>
            <GameImage src={myVideogame.image} alt="img not found" />
            <ContainerTitleyInfo>
            <GameTitle>{myVideogame.name}</GameTitle>
            <GameInfo>
              <ContLT>
                <InfoLabel>Plataformas: </InfoLabel>
                <InfoText>
                  {detailPlatform ? detailPlatform : "No tiene plataformas"}
                </InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Generos: </InfoLabel>
                <InfoText>{detailGenres}</InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Lanzamiento: </InfoLabel>
                <InfoText>{myVideogame.released}</InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Rating: </InfoLabel>
                <InfoText>{myVideogame.rating}</InfoText>
              </ContLT>
            </GameInfo>
            </ContainerTitleyInfo>
          </ContainerGetInfo>


          <GameDescription>
            <p>{myVideogame.description}</p>
          </GameDescription>

          <Link to="/home">
            <Button>Volver</Button>
          </Link>

        
        </div>
      ) : (
        <Loading />
      )}
    </DetailContainer>
  );
}


const Image = styled.img`
z-index: 0;
  height: 100vh;
  
  width: 100vw;
  position: fixed;
  top: 0;
  left: 0;
`;

const Button = styled.button`
  background-color: #ffc300;
  font-weight: bold;
  color: white;
  padding: 10px 4rem;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const DetailContainer = styled.div`
  margin: 10rem;

  @media (max-width: 950px) {
    margin: 1rem;
  }
`;

const DetailHeader = styled.div``;

const ContainerTitleyInfo = styled.div `
display: flex;
flex-direction: column;

`

const GameTitle = styled.h2``;



const ContainerGetInfo = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  gap: 5rem;


  @media (max-width: 950px) {
    flex-direction: column;
  }
`;

const GameImage = styled.img`
  width: 40rem;
  height: auto;
  border-radius: 2%;
  overflow: hidden;
  transition: 0.8s;

  &:hover {
    transform: scale(1.05);
    color: #fff;
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #fff9f9ea, 0 0 10px #fff9f9ea;
  }

  @media (max-width: 950px) {
    min-width: 20rem;
  }
`;

const GameInfo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  min-width: 20rem;
`;

const ContLT = styled.div`
  display: flex;
  aling-items: center;
  justify-content: space-between;
  gap: 1rem;
  min-width: 20rem;
`;

const InfoLabel = styled.label`
  font-weight: bold;
`;

const InfoText = styled.p`
  margin: 0;
`;

const GameDescription = styled.div``;
