const initialState = {
    videogames: []
}

function rootReducer (state = initialState, action){
    switch(action.type) {
        case 'GET_VIDEOGAMES':
            return {
                ...state,
                videogames: action.payload
            }

        case 'FILTER_BY_GENRES':
            const allVideogames = state.videogames
            const genresFiltered = action.payload === 'All' ? allVideogames : allVideogames?.filter(e => {
                if(!allVideogames.genres) return undefined;
                return videogames.genres.split(",").includes(action.payload)
            })
            return{
                ...state,
                videogames: genresFiltered
            }
            
           
           
           
           
                //     e.genres.includes(action.payload))
