const initialState = {
    videogames: [],
    allVideogames: [],
    allgenres: [],
    platforms: [],
    details: {},
};

function rootReducer (state = initialState, action) {
    switch (action.type) {

        case "CASE_VIDEOGAMES":
            let platforms = [];
            action.payload.map((element) => (platforms = [...platforms, ...element.platforms]));
            return {
                ...state,
                videogames: [...action.payload],
                allVideogames: [...action.payload],
                platforms: Array.from(new Set(platforms)),
                page: 1,
            };

        case "GET_NAME_VIDEOGAMES":
            return {
                ...state, 
                allgenres: action.payload,
            };

        case "GET_DETAILS":
            return {
                ...state,
                details: action.payload
            };
        
        case "CLEAR":
            return {
                ...state,
                details: action.payload
            }
        
        case "FILTER_BY_GENRES":
            const allVideogames2 = state.allVideogames;
            const genresFilter = action.payload === "All"
            ? allVideogames2
            : allVideogames2.filter((element) => element.genres.includes(action.payload));
        return {
            ...state,
            videogames: genresFilter,
        };

        case "OREDER_BY_RATING":
            const sorteArrRating = action.payload === "least"
            ? state.videogames.sort(function (a, b) {
                if (a.rating > b.rating) {
                    return 1;
                }
                if (b.rating > a.rating) {
                    return -1;
                }
                return 0;
            })
            : state.videogames.sort(function (a, b) {
                if (a.rating > b.rating) {
                    return -1;
                }
                if (b.rating > a.rating) {
                    return 1;
                }
                return 0;
            });
            return {
                ...state,
                videogames: sorteArrRating,
            };

            case "ORDER_BY_NAME":
                let sortedArr = 
                action.payload === 'asc'
                ? state.videogames.sort(function (a, b) {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) {
                        return 1;
                    }
                    if (b.name.toLowerCase() > a.name.toLowerCase()){
                        return -1;
                    }
                    return 0;
                })
                : state.videogames.sort(function (a, b) {
                    if(a.name.toLowerCase() > b.name.toLowerCase()){
                        return -1;
                    }
                    if (b.name.toLowerCase() > a.name.toLowerCase()){
                        return 1;
                    }
                    return 0;
                });
                return {
                    ...state,
                    videogames: sortedArr,
                };

                default: return state;
    }
}


export default rootReducer;
