import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

function Card({
  id,
  name,
  description,
  platforms,
  image,
  released,
  rating,
  genres,
  background_image,
}) {
  return (
    <div id="card">
      <Title>{name}</Title>
      <img
        src={image ? image : background_image}
        alt="Img not found"
        width="300px"
        height="200px"
      />
      <Subtitle>Género: {genres?.join(", ")}</Subtitle>
      <Subtitle className="rating"> Rating: {rating}</Subtitle>
      <Link to={`/details/${id}`}>
        <button id="MoreInfo">Más información</button>
      </Link>
    </div>
  );
}

const Title = styled.h3`
  color: green;
`;
const Subtitle = styled.h5`
  color: red;
  .rating {
    font-size: 400px;
  }
`;

export default Card;
