import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <mainDetail>

      <div className={styles.containerDetail}>
        {/* <h1>ID: {id}</h1> */}
        <div className={styles.title}>
          <h1>{name}</h1>
        </div>

        <div className={styles.gameAbout}>
          <h2>About</h2>
          <div className={styles.about}>
            <p dangerouslySetInnerHTML={{ __html: description }} />
          </div>
        </div>

        <div className={styles.infoDetail}>

          <div>
            <h2>Platforms </h2>
            {
              platforms?.map((plat, i) => {
                return (
                  <p key={i}>{plat.name}</p>
                )
              })
            }
          </div>

          <div>
            <h2>Genres </h2>
            {
              genres?.map((gen, i) => {
                return (
                  <p key={i}>{gen.name}</p>
                )
              })
            }
          </div>

          <div>
            <h2>Released</h2>
            <p>{released}</p>
          </div>

          <div>
            <h2>Rating</h2>
            <p>{rating}</p>
          </div>

          {created ? <div className={styles.btnContainer}> <button className={`${styles.btn} ${styles.create}`} onClick={() => deleteById(id)}>Delete</button> </div> : null}

          <br />
          <div className={styles.btnContainer}>
            <Link to="/home" ><button className={styles.btn}>Back</button></Link>
          </div>

        </div>

      </div>

      <div className={styles.imageContainer}>
        <img className={styles.imgDetail} src={image} alt={name} />
      </div>
    </mainDetail>
  );
};

const DetailContainer = styled.div`
width: 100%;
height:100%;
display: flex;
flex-direction: row;
background-color: #151515;
  `;

const GameImage = styled.img`
position: relative;
  border-radius: 5px;
  width: 260px;
  height: 100%;
  object-fit: cover;
`;

const GameTitle = styled.h2`
  margin-bottom: 3rem;
  margin-top: -24rem;
  margin-left: 35rem;
  font-size: 60px;
`;

const InfoContainer = styled.div`
  display: flex;
  margin-top: 1rem;
`;

const InfoLabel = styled.label`
  display: flex;
  font-weight: bold;
  width: 10rem;
  flex-direction: flex-start;
  margin-left: 60.5rem;
  margin-top: 0rem;
  font-size: 20px;
`;

const InfoText = styled.p`
  display: flex;
  justify-content: flex-end;
  margin-right: 30rem;
  margin-top: 0rem;
  font-size: 20px;
`;

const GameDescription = styled.div`
z-index: 1;
  display: flex;
  justify-content: center;
  margin-top: 1.5rem;
  margin-left: 17.5rem;
  border: 2px solid #ffc300;
  padding: 01rem;
  max-width: 31.5%;
  text-align: left;
`;

const Button = styled.button`
  position: absolute;
  top: 51rem;
  right: 36rem;
  background-color: #ffc300;
  font-weight: bold;
  color: white;
  padding: 10px 4rem;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #555;
  }
`;
