import React from "react";
import { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postVideogames, getPlatforms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";

export default function VideogameCreate() {
  const dispatch = useDispatch();
  const platforms = useSelector((state) => state.platforms);
  const [platformOptions, setPlatformOptions] = useState([]);

  const [input, setInput] = useState({
    name: "",
    description: "",
    released: "",
    rating: "",
    image: "",
    platforms: [],
    genres: [],
  });

  useEffect(() => {
    dispatch(getPlatforms())
      .then((platforms) => {
        setPlatformOptions(
          platforms.map((platform) => ({
            value: platform.id,
            label: platform.name,
          }))
        );
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);
  return (
    <div>
      <Link to="/home">
        <button>Volver a inicio</button>
      </Link>
      <h1>¡Creá tu videojuego!</h1>
      <form>
        <div>
          <label>Nombre:</label>
          <imput type="text" value={input.name} name="name" />
        </div>
        <div>
          <label>Descripción:</label>
          <input type="text" value={input.description} name="description" />
        </div>
        <div>
          <label>Fecha de lanzamiento:</label>
          <input type="date" value={input.released} name="released" />
        </div>
        <div>
          <label>Rating:</label>
          <input type="text" value={input.rating} name="rating" />
        </div>
        <div>
          <label>Imagen:</label>
          <input type="text" value={input.image} name="imagen" />
        </div>
    
        <div>
          <label>Géneros:</label>
          <input type="text" value={input.genres} name="genres" />
        </div>
        <select>
        {platformOptions.map((option) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      </form>
    </div>
  );
}
