import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <DetailContainer>
      
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <ContainerGetInfo>
            <GameImage src={myVideogame.image} alt="img not found" />
            <ContainerTitleyInfo>
            <GameTitle>{myVideogame.name}</GameTitle>
            <GameInfo>
              <ContLT>
                <InfoLabel>Plataformas: </InfoLabel>
                <InfoText>
                  {detailPlatform ? detailPlatform : "No tiene plataformas"}
                </InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Generos: </InfoLabel>
                <InfoText>{detailGenres}</InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Lanzamiento: </InfoLabel>
                <InfoText>{myVideogame.released}</InfoText>
              </ContLT>
              <ContLT>
                <InfoLabel>Rating: </InfoLabel>
                <InfoText>{myVideogame.rating}</InfoText>
              </ContLT>
            </GameInfo>
            </ContainerTitleyInfo>
            <GameDescription>
            <p>{myVideogame.description}</p>
          </GameDescription>
          </ContainerGetInfo>


          

          <Link to="/home">
            <Button>Volver</Button>
          </Link>
        </div>
      ) : (
        <Loading />
      )}
    </DetailContainer>
  );
}

const Container = styled.div`
.container{
  border-radius: 4rem;
  padding: 2rem;
  margin-top: 3rem;
  display: flex;
  /* align-items: center; */
  border: solid 1px #fff;
  box-shadow: 1px 1px 1px 1px #fff9f9ea;
  color: #fff;
  position: relative;
  top: 0%;
  right: 0%;
  transition: all 400s;
  background-image: linear-gradient(0deg, #81afef60, transparent);
}

&:hover{
  box-shadow: 5px 5px 20px #faf9f9e3;
}
`;

const Wrapper = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 20px;
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
  margin-right: 20px;
`;

const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Title = styled.h2`
  font-size: 28px;
  margin-bottom: 10px;
`;

const Label = styled.label`
  font-weight: bold;
`;

const Button = styled.button`
margin-left: 1rem;
border-radius: 10px;
font-size: 1.4rem;
margin-top: .5rem;
width: 5rem;
background: rgb(53, 201, 206);
border: solid 1px rgb(2, 243, 252);
color: #fff;
letter-spacing: 1px;
overflow: hidden;
transition: 0.8s;
}

&:hover{
cursor: pointer;
transform: scale(1.1);
background-color: rgb(2, 243, 252);
box-shadow: 0 0 10px rgb(2, 243, 252), 0 0 40px rgb(2, 243, 252);
}
`
