import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
    getVideogames,
    filterByGenres,
    getGenres,
    filterCreated,
    orderByRating,
    orderByName,
} from "../actions/index"
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";
import Loading from "./Loading";
import NotFound from "./NotFound";
//IMPORTAR ESTILOS

export default function Home() {
  const dispatch = useDispatch();
  const allVideogames = useSelector((state) => state.videogames);
  const allgenres = useSelector((state) => state.allgenres);
  const [loading, setLoading] = useState(true);
  const [OrdersetOrden] = useState("");
  const [setOrdenRating] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [videogamePerPage] = useState(15);
  const indexOfLastVideogame = currentPage * videogamePerPage;
  const indexOfFirtsVideogame = indexOfLastVideogame - videogamePerPage;
  const currentVideogame = allVideogames.slice(
    indexOfFirtsVideogame,
    indexOfLastVideogame
  );

  useEffect(() => {
    if(allVideogames.length > 0 ){
      setLoading(!loading)
    }
  }, [allVideogames, loading]) 

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  if (allVideogames.length > 0 && loading) {
    setLoading(false);
  }

  useEffect(() => {
    console.log("algo")
    dispatch(getVideogames());
    dispatch(getGenres());
  }, [dispatch]);

  function handleClick(element) {
    element.preventDefault();
    dispatch(getVideogames());
  }

  function handlePrev(element) {
    element.preventDefault();
    setCurrentPage(currentPage - 1);
  }

  function handleNext(element) {
    element.preventDefault();
    setCurrentPage(currentPage + 1);
  }

  function handleFilterGenres(element) {
    dispatch(filterByGenres(element.target.value));
    setCurrentPage(1);
  }
  function handleFilterCreated(element) {
    element.preventDefault();
    dispatch(filterCreated(element.target.value)); //PARA CREAR UN NUEVO JUEGO
  }
  function handleRating(e) {
    e.preventDefault();
    dispatch(orderByRating(e.target.value));
    setCurrentPage(1);
    setOrdenRating(e.target.value);
  }

  function handleSort(element) {
    element.preventDefault();
    dispatch(sortAlphabetically(e.target.value));
    setCurrentPage(1);
    setOrder(`Sorted by: ${e.target.value}`);
    setHeader(`Sorted by: ${e.target.value}`);
  }
  return (
    <div id="HomeGeneral">
      <h1 id="titulo">Elige tu juego!</h1>
      <Link to="/videogames">
        <button id="btnCrear">Crear juego nuevo</button>
      </Link>
      <button
        id="recargar"
        onClick={(element) => {
          handleClick(element);
        }}
      >
        Recargar juego{" "}
      </button>
      <div>
        <SearchBar />
        <div onChange={(element) => handleFilterGenres(element)}>
          <select id="select">
            <option value="todos">Todos los géneros</option>
            {allgenres?.map((element) => {
              return (
                <option key={element.id} value={element.value}>
                  {element.name}
                </option>
              );
            })}
          </select>
        </div>
        <select id="select" onChange={(element) => handleSort(element)}>
          <option>Orden </option>
          <option value="asc">Ascendente </option>
          <option value="desc">Descendente</option>
        </select>
        <select
          id="select"
          onChange={(element) => handleRating(element)}>
             <option>Select Rating</option>
          <option value="least">Menos Popular</option>
          <option value="most">Más Popular</option>
        </select>
        <select id="select" onChange={(e) => handleFilterCreated(e)}>
          <option value="todos">Todos los videos juegos</option>
          <option value="existentes">Existente</option>
          <option value="creados">Creados</option>
        </select>

        <div id="cardHome">
          {currentVideogame?.length > 0 && !loading ? (
            currentVideogame?.map((element) => {
              return (
                <Card
                  key={element.id}
                  id={element.id}
                  name={element.name}
                  image={
                    element.image ? element.image : element.background_image
                  }
                  genres={element.genres}
                  rating={element.rating}
                  platforms={element.platforms}
                  released={element.released}
                  description={element.description}
                />
              );
            })
          ) : !currentVideogame.length > 0 && loading ? (
            <Loading />
          ) : (
            <NotFound />
          )}
        </div>

        <Paginado
          videogamePerPage={videogamePerPage}
          allVideogames={allVideogames.length}
          paginado={paginado}
          currentPage={currentPage}
        />
        <button
          id="pag"
          onClick={(element) => handlePrev(element)}
          disabled={currentPage <= 1}
        >
          {" "}
          Prev{" "}
        </button>

        <button
          id="pag"
          onClick={(element) => handleNext(element)}
          disabled={currentVideogame.length < 15}
        >
          {" "}
          Next{" "}
        </button>
      </div>
    </div>
  );
}
