import React from "react";
import { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postVideogames, getPlatforms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";

export default function VideogameCreate() {
  const dispatch = useDispatch();
  const platforms = useSelector((state) => state.platforms);

  const [input, setInput] = useState({
    name: "",
    description: "",
    released: "",
    rating: "",
    image: "",
    platforms: [],
    genres: [],
  });

  function handleChange(e){
    setInput({
        ...input,
        [e.target.name] : e.target.value
    })
    console.log(input)
  }

  useEffect(() => {
    dispatch(getPlatforms());
  }, [dispatch]);

  return (
    <div>
      <Link to="/home">
        <button>Volver a inicio</button>
      </Link>
      <h1>¡Creá tu videojuego!</h1>
      <form>
        <div>
          <label>Nombre:</label>
          <imput type="text" value={input.name} name="name" />
        </div>
        <div>
          <label>Descripción:</label>
          <input type="text" value={input.description} name="description" />
        </div>
        <div>
          <label>Fecha de lanzamiento:</label>
          <input type="date" value={input.released} name="released" />
        </div>
        <div>
          <label>Rating:</label>
          <input type="text" value={input.rating} name="rating" />
        </div>
        <div>
          <label>Imagen:</label>
          <input type="text" value={input.image} name="imagen" />
        </div>
        <div>
          <label>Géneros:</label>
          <input type="text" value={input.genres} name="genres" />
        </div>
        <select>
            {platforms.map((plat) => (
                <option value={plat.name}>{plat.name}</option>
            ))}
        </select>

                <button type='submit'>Crear videojuego</button>

      </form>
    </div>
  );
}
