import React from "react";
import styled from "styled-components";
import { useEffect, useState } from "react";

export default function Paginado({ videogamesPerPage, allVideogames, paginado }) {
  const [activePage, setActivePage] = useState(1);
  
  useEffect(() => {
  setActivePage(1);
  }, [allVideogames]);
  
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(allVideogames / videogamesPerPage); i++) {
  pageNumbers.push(i);
  }
  
  const handleClick = (pageNumber) => {
  setActivePage(pageNumber);
  paginado(pageNumber);
  };
  
  const startIndex = (activePage - 1) * videogamesPerPage;
  const endIndex = startIndex + videogamesPerPage;
  const visibleVideogames = allVideogames.slice(startIndex, endIndex);
  
  return (
  <Container>
  {pageNumbers.map((pageNumber) => (
  <Button
  key={pageNumber}
  onClick={() => handleClick(pageNumber)}
  active={pageNumber === activePage}
  >
  {pageNumber}
  </Button>
  ))}
  {visibleVideogames.map((videogame) => (
  <Card key={videogame.id} videogame={videogame} />
  ))}
  </Container>
  );
  }

  const Card = styled.div`
  `
  const Button = styled.div`
  `
  const Container = styled.div`
  `
const Nav = styled.nav`
  display: flex;
  justify-content: center;
  position: absolute;
  bottom: -65%;
  left: 49%;
  transform: translateX(-50%);

`;

const Ul = styled.ul`
  list-style: none;
  display: flex;
  gap: 0.5rem;
`;

const Li = styled.li`
  button {
    background-color: #FFB20D;
    border: none;
    color: black;
    padding: 0.5rem 1rem;
    cursor: pointer;
    &:hover {
      background-color: #555;
    }
  }
`;
