import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogames, filterGenres  } from "../redux/actions";
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginado from "./Paginado";

export default function Home() {
  const dispatch = useDispatch();
  const allVideogames = useSelector((state) => state.videogames);
  const [currentPage, setCurrentPage] = useState(1)
  const [videogamesPerPage, setVideogamesPerPage] = useState(15)
  const indexOfLastVideogame = currentPage * videogamesPerPage
  const indexOfFirstVideogame = indexOfLastVideogame - videogamesPerPage
  const currentVideogames = allVideogames?.slice(indexOfFirstVideogame, indexOfLastVideogame)
  const videogames = useSelector((state) => state.videogames);
  const [order, setOrder] = useState("");
  const allGenres = useSelector((state) => state.genres);

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber)
  }

  useEffect(() => {
    dispatch(getVideogames());
  }, [dispatch]);

  function handleClick(e) {
    e.preventDefault();
    dispatch(getVideogames());
  }

  function handleGenres(e){
    //El e.preventDefault(), no lo vamos a necesitar en esta funcion
    dispatch(filterGenres(e.target.value));
    setOrder(`${e.target.value}`);
 }

  return (
    <div>
      <Link to="/videogames">Crear juego nuevo</Link>
      <h1>Mattger GAMES</h1>
      <button
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Recargar todos los juegos
      </button>
      <div>
        <select>
            <option value="asc">Ascendente</option>
            <option value="desc">Descendente</option>
        </select>
        <select>
            <option value="Order-Rating">Ordenar por rating</option>
            <option value="Men-May">Menor-Mayor</option>
            <option value="May-Men">Mayor-Menor</option>
        </select>
        <select onChange={e => handleGenres(e)} >
                       <option value="All">Genres</option>
                       {
                          allGenres?.map(el => (<option key={el.id} value={el.name}>{el.name}</option>))
                        }
                    </select>
        <select >
            <option value="All">All</option>
            <option value="Created">Creados</option>
            <option value="Existing">Existentes</option>
        </select>
        {allVideogames && (
        <Paginado
        videogamesPerPage={videogamesPerPage}
        allVideogames={allVideogames.length}
        paginado = {paginado}
        />
        )}
        {currentVideogames?.map((e) => {
            return(
              <div key={e.id}>
                    <Link to={'/home/' + e.id}>
                <Card name={e.name} genres={e.genres} rating={e.rating} image={e.image}/>
                    </Link>
              </div>      
            );
            })}
      </div>
    </div>
  );
}
