import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <MainDetail>

      <ContainerDetail >
        {/* <h1>ID: {id}</h1> */}
        <Title >
          <h1>{name}</h1>
        </Title>

        <GameAbout>
          <h2>About</h2>
          <About>
            <p dangerouslySetInnerHTML={{ __html: description }} />
          </About>
        </GameAbout>

        <InfoDetail>

          <div>
            <h2>Platforms </h2>
            {
              platforms?.map((plat, i) => {
                return (
                  <p key={i}>{plat.name}</p>
                )
              })
            }
          </div>

          <div>
            <h2>Genres </h2>
            {
              genres?.map((gen, i) => {
                return (
                  <p key={i}>{gen.name}</p>
                )
              })
            }
          </div>

          <div>
            <h2>Released</h2>
            <p>{released}</p>
          </div>

          <div>
            <h2>Rating</h2>
            <p>{rating}</p>
          </div>

          {created ? <div className={styles.btnContainer}> <button className={`${styles.btn} ${styles.create}`} onClick={() => deleteById(id)}>Delete</button> </div> : null}

          <br />
          <BtnContainer >
            <Link to="/home" ><Btn>Back</Btn></Link>
          </BtnContainer>

        </InfoDetail>

      </ContainerDetail>

      <ImageContainer >
        <ImgDetail  src={image} alt={name} />
      </ImageContainer>
    </MainDetail>
  );
};

const MainDetail = styled.div`

  `;

const ContainerDetail = styled.img`

`;

const Title = styled.h2`
 
`;

