import React from "react";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogames, filterVideogamesBySatatus } from "../redux/actions";
import { Link } from 'react-router-dom';
import Card from "./Card";
import Paginado from "./Paginado";

export default function Home (){

const dispatch = useDispatch()
const allVideogames = useSelector ((state) => state.allVideogames)
const [currentPage, setCurrentPage] = useState(1)
const [videogamePerPage, setVideogamePerPage] = useState (20) //Controla cuántos videojugos se muestran por página
const indexOfLastVideogame = currentPage * videogamePerPage
const indexOfFirtsVideogame = indexOfLastVideogame - videogamePerPage
const currentVideogames = allVideogames.slice(indexOfFirtsVideogame, indexOfLastVideogame)

const paginado = (pageNumber) => {
  setCurrentPage(pageNumber)
}

useEffect (() => {
  dispatch(getVideogames());
},[dispatch])

function handleClick(element){
  element.preventDefault();
  dispatch(getVideogames());
}



return (
  <div>
    <Link to='/videogames'>Crea tu videojuego</Link>
    <h1>Mattger GAMES</h1>
   <button onClick={element => {handleClick(element)}}>
    Recargar todos los juegos
    </button>
    <div>
      <select>
        <option value="asc">Ascendente</option>
        <option value="desc">Descendente</option>
      </select>
      <select>
        <option value="rating">Select Rating</option>
        <option value="least">Menos Popular</option>
        <option value="more">Más Popular</option>
      </select>
      <select>
        <option value="All">Todos los videos juegos</option>
        <option value="existente">Existentes</option>
        <option value="creados">Creados</option>
      </select>
      <Paginado
      videogamesPerPage={videogamePerPage}
      allVideogames={allVideogames.length}
      paginado = {paginado}
      />
      {currentVideogames?.map((videogame) => {
        return (
          <div>
            <Link to={"/home/" + videogame.id}>
              <Card  image={videogame.image}
                        name={videogame.name}
                        rating={videogame.rating}
                        genres={videogame.genres}/>
            </Link>
          </div>
        )
      })}
    </div>
    </div>

    
)
}
