import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <div>
    {Object.keys(myVideogame).length > 0 && !loading ? (
      <Container>
        <Wrapper>
          <Image src={myVideogame.image} alt="" />
          <InfoContainer>
            <Title>{myVideogame.name}</Title>
            <div>
              <div>
                <Label>Plataformas: </Label>
                <p>{detailPlatform}</p>
              </div>
              <div>
                <Label>Géneros: </Label>
                <p>{detailGenres}</p>
              </div>
              <div>
                <Label>Lanzamiento: </Label>
                <p>{myVideogame.released}</p>
              </div>
              <div>
                <Label>Rating: </Label>
                <p>{myVideogame.rating}</p>
              </div>
            </div>
          </InfoContainer>
        </Wrapper>

        <div>
          <p>{myVideogame.description}</p>
        </div>

        <Link to="/home">
          <BackButton>Volver</BackButton>
        </Link>
      </Container>
    ) : (
      <Loading />
    )}
  </div>
);
}

const Container = styled.div`
.container{
  border-radius: 4rem;
  padding: 2rem;
  margin-top: 3rem;
  display: flex;
  /* align-items: center; */
  border: solid 1px #fff;
  box-shadow: 1px 1px 1px 1px #fff9f9ea;
  color: #fff;
  position: relative;
  top: 0%;
  right: 0%;
  transition: all 400s;
  background-image: linear-gradient(0deg, #81afef60, transparent);
}

.container:hover{
  box-shadow: 5px 5px 20px #faf9f9e3;
}
`;

const Wrapper = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 20px;
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
  margin-right: 20px;
`;

const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Title = styled.h2`
  font-size: 28px;
  margin-bottom: 10px;
`;

const Label = styled.label`
  font-weight: bold;
`;

const BackButton = styled.button`
  margin-top: 20px;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #f2f2f2;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    background-color: #e0e0e0;
  }
`;
