import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <MainDetail>
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <ContainerDetail>
            <Title>{myVideogame.name}</Title>
            <GameAbout>
          <H2About>Descripci√≥n</H2About>
          <About>
            <p dangerouslySetInnerHTML={{ __html: myVideogame.description }} />
          </About>
        </GameAbout>
            <InfoDetail>
              <div>
                <InfoDetailH2>Plataformas: </InfoDetailH2>
                <InfoDetailP>
                  {detailPlatform ? detailPlatform : "No tiene plataformas"}
                </InfoDetailP>
              </div>
              <div>
              <InfoDetailH2>Generos: </InfoDetailH2>
              <InfoDetailP>{detailGenres}</InfoDetailP>
              </div>
              <div>
              <InfoDetailH2>Lanzamiento: </InfoDetailH2>
              <InfoDetailP>{myVideogame.released}</InfoDetailP>
              </div>
              <div>
              <InfoDetailH2>Rating: </InfoDetailH2>
              <InfoDetailP>{myVideogame.rating}</InfoDetailP>
              </div>
              <br />
              <Link to="/home">
                <Button>Volver</Button>
              </Link>
            </InfoDetail>
          </ContainerDetail>
          <ImageDetail src={myVideogame.image} alt="img not found" />
        </div>
      ) : (
        <Loading />
      )}
    </MainDetail>
  );
}


const MainDetail = styled.div`
width: 100%;
height:100%;
display: flex;
flex-direction: row;
background-color: #151515;
`;

const ContainerDetail = styled.div`
color: rgb(237, 237, 237);
  padding-top: 120px;
  width: 100%;
  height: 820px;
  position: fixed;
  z-index: 1;
  background: linear-gradient(-90deg, #000000, #00000000);
`;

const Title = styled.div`
font-size: 80px;
  line-height: 70px;
  font-weight: 800;
  text-shadow: -3px 3px #FFC300;
  margin-left: 80rem;
`;

const GameAbout = styled.div`
margin-top: 32px;
  margin-left: 30px;
`;

const H2About = styled.h2` 
color: #FFC300;
  text-shadow: 1px 1px #000000;
  margin: 0 0 8px;
  margin-left: 50rem;
`

const About = styled.div`
display: flex;
width: 500px;
height: 320px;
line-height: 22px;
overflow-y: scroll;
text-align: left;
margin-left: 80rem;


`;

const InfoDetail = styled.div`
display: flex;
  margin: 2px 4px  4px 20px;
  justify-content: space-evenly
  
`;

const InfoDetailH2 = styled.h2`
margin-bottom: 10px;
  color: #FFC300;
  text-shadow: 1px 1px #000000;
`

const InfoDetailP = styled.p `
  display: flex;
  flex-direction: column;
`

const Button = styled.button`
width: 200px;
height: 40px;
font-size: 1.2rem;
color: black;
outline: 0;
border: 0;
border-radius: 8px;
background-color: #FFC300;
border: solid 1px black;
cursor: pointer;

&:hover {
  background-color: black;
  color: #FFC300;
  transition: all 0.2s ease;
}
`;
const ImageDetail = styled.img`
position: fixed;
width: 100%;
height: 100%;
object-fit: cover;
}
`;
