export default function validation(input){
    //Defino un objeto al cual le vamos a pasar todos los errores que detectemos
    let errors = {};

    //Valido 
    if(!input.name){
        errors.name = "Se requiere un nombre";
    }else if(!/^[a-zA-Z0-9-() .]+$/.test(input.name)){
        errors.name = "Solo se acepta letras, números, guiones y paréntesis"
    }else if(input.name.length > 20){
        errors.name = "El nombre ha excedido los 20 carácteres permitidos";
    }
    if(!input.description){
        errors.description = "Se requiere un descripción";
    }else if(input.description.length > 100){
        errors.description = "La descripción tiene un máximo de 100 carcteres";
    }
    if(!input.released){
        errors.released = "The date is required";
    }
    if(!input.rating){
        errors.rating = "The rating is required";
    }else if(input.rating >= 5 || input.rating <= 0){
        errors.rating = "The rating must be between 0 to 5";
    }
    if(!input.image){
        errors.image = "The image URL is required";
    }
    if(!input.platforms){
        errors.platforms = "Minimum one platform requered";
    }
    return errors;
}
