import React from "react";
import { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postVideogames, getPlatforms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";
import validation from "../redux/validations";

export default function VideogameCreate() {
  const dispatch = useDispatch();
  const history = useHistory();
  const platformsList = useSelector((state) => state.platforms);

  const [input, setInput] = useState({
    name: "",
    description: "",
    released: "",
    rating: "",
    image: "",
    platforms: [],
    genres: [],
  });

  const [errors, setErrors] = useState({})

  function handleChange(e){
    setInput({
        ...input,
        [e.target.name] : e.target.value
    })
    console.log(input)
  }

  function handleSelect(e){
    if (!input.platforms.includes(e.target.value)){
        setInput({
            ...input,
            platforms: [...input.platforms, e.target.value]
        })
        setErrors(validation({
          ...input,
          platforms: [...input.platforms, e.target.value]
      }))
  }else{
      setInput({
          ...input
      })
  }
}

function handleDeletePlatforms(e){
  setInput({
      ...input,
      platforms: input.platforms.filter(el => el !== e)
  })
}


  function handleSubmit(e){
    e.preventDefault();
    dispatch(postVideogames(input))
    alert("Tu videojuego ha sido creado")
    setInput({
      name: "",
    description: "",
    released: "",
    rating: "",
    image: "",
    platforms: [],
    genres: []
    })
    history.push('/home')
  }

//   function handleCheck(e){
//     if(e.target.checked){
//         setInput({
//             ...input,
//             status: e.target.value 
//         })
//     }
//   }

  useEffect(() => {
    dispatch(getPlatforms());
  }, [dispatch]);

  return (
    <div>
      <Link to="/home">
        <button>Volver a inicio</button>
      </Link>
      <h1>¡Creá tu videojuego!</h1>
      <form onSubmit={(e) => handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input type="text" value={input.name} name="name" onChange={handleChange} />
        </div>
        <div>
          <label>Descripción:</label>
          <input type="text" value={input.description} name="description" onChange={handleChange} />
        </div>
        <div>
          <label>Fecha de lanzamiento:</label>
          <input type="date" value={input.released} name="released" onChange={handleChange} />
        </div>
        <div>
          <label>Rating:</label>
          <input type="text" value={input.rating} name="rating" onChange={handleChange} />
        </div>
        <div>
          <label>Imagen:</label>
          <input type="text" value={input.image} name="imagen" onChange={handleChange} />
        </div>
        <div>
          <label>Géneros:</label>
          <input type="text" value={input.genres} name="genres" onChange={handleChange} />
        </div>
        <div>
        <h6>Plataformas: </h6>
        <select onChange={(e) => handleSelect(e)}>
        <option value="All">All</option>
            {platformsList && platformsList.map((platform) => {
              return (
                <option key={platform} value={platform}>{platform}</option>
              )
              })}
        </select>
        {errors.platforms && <p>{errors.platforms}</p>}
        </div>
        <div>
            {input.platforms.map((e) => (
              <li>
                <div>
                  {e + " "}
                  <button type="button" onClick={() => handleDeletePlatforms(e)}>X</button>
                </div>
              </li>
            ))}
        </div>
      </form>
    </div>
  );
}
