import React from "react";
import styled from "styled-components";

const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const CardWrapper = styled.div`
  flex-basis: calc(33.33% - 10px); /* Ancho de la carta */
  margin: 5px;
`;

const Card = ({id, name, genres, rating, image}) => {
  return (
    <CardWrapper>
      <img src={image} alt={name} />
      <div>
        <h3>{name}</h3>
        <h5>GÃ©neros: {genres}</h5>
        <h5>Rating {rating}</h5>
      </div>
    </CardWrapper>
  );
};

export default function CardList({cards}) {
  return (
    <CardContainer>
      {cards.map(card => (
        <Card key={card.id} {...card} />
      ))}
    </CardContainer>
  );
}
