import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails} from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";


export default function Details(){
    const [loading, setLoading] = useState(true);
    const dispatch = useDispatch()
    const { id } = useParams();

    useEffect(() => {
        dispatch(getDetails(id));
    }, [dispatch, id])

    const myVideogame = useSelector ((state) => state.detail)
    const detailPlatform = myVideogame.platforms?.join('-');
    const detailGenres = myVideogame

    return(
        <div>
            {
               myVideogame && Object.keys(myVideogame).length > 0
            }
                <div>
                    <h1>{myVideogame.name}</h1>
                    <img src={myVideogame.img ? myVideogame.img : myVideogame.image}/>
                    <h2>Released: {myVideogame.released}</h2>
                    <div>
                    <label>Plataformas: </label>
                    <p>{detailPlatform? detailPlatform:"No tiene plataformas"}</p>
                </div>
        </div> : <Loading/>
            
            <Link to= '/home'>
                <button>Volver</button>
            </Link>
        </div>
    )
    
}
