const initialState = {
    videogames: [],
    allVideogames: []
}

function rootReducer (state = initialState, action){
    switch(action.type) {
        case 'GET_VIDEOGAMES':
            return {
                ...state,
                videogames: action.payload,
                allVideogames: action.payload
            }

        case 'FILTER_BY_GENRES':
            const allVideogames = state.allVideogames
            const genresFiltered = action.payload === 'All' ? allVideogames : allVideogames.filter(e => e.genres.includes(action.payload));
            return {
                ...state,
                videogames: genresFiltered
            }
            case 'FILTER_CREATED':
                const allVideogames2 = state.allVideogames
                const createdFilter = action.payload === 'created' ? allVideogames.filter (e => e.createdInDb) : allVideogames.filter(e => !e.createdInDb)
                return{
                    ...state,
                    videogames: action.payload === 'All' ? state.allVideogames : createdFilter
                }
            default: 
            return state;
    }
}

export default rootReducer;
