import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <MainDetail>
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <ContainerDetail>
            <Title>{myVideogame.name}</Title>
            <InfoDetail>
              <div>
                <h2>Plataformas: </h2>
                <p>
                  {detailPlatform ? detailPlatform : "No tiene plataformas"}
                </p>
              </div>
              <div>
              <h2>Generos: </h2>
              <p>{detailGenres}</p>
              </div>
              <div>
              <h2>Lanzamiento: </h2>
              <p>{myVideogame.released}</p>
              </div>
              <div>
              <h2>Rating: </h2>
              <p>{myVideogame.rating}</p>
              </div>
              <Link to="/home">
                <Button>Volver</Button>
              </Link>
            </InfoDetail>

            <GameDescription>
              <p>{myVideogame.description}</p>
            </GameDescription>
          </ContainerDetail>
          <GameImage src={myVideogame.image} alt="img not found" />
        </div>
      ) : (
        <Loading />
      )}
    </MainDetail>
  );
}

const Button = styled.button``;

const DetailContainer = styled.div`
  color: rgb(237, 237, 237);
  padding-top: 120px;
  width: 100%;
  height: 803px;
  position: fixed;
  z-index: 1;
  background: linear-gradient(90deg, #000000, #00000000);
`;

const DetailHeader = styled.div``;

const ContainerTitleyInfo = styled.div``;

const GameTitle = styled.h2`
  font-size: 30px;
  line-height: 70px;
  font-weight: 700;
  text-shadow: -4px 4px rgb(0, 0, 0);
  margin-left: 30px;
`;

const ContainerGetInfo = styled.div``;

const GameImage = styled.img`
margin-top: 77px;
  position: fixed;
  width: 100%;
  height:900px;
  object-fit: cover;
}
`;

const GameInfo = styled.div`
  display: flex;
  margin: 25px 4px 4px 20px;
  justify-content: space-evenly;
`;

const ContLT = styled.div``;

const InfoLabel = styled.label``;

const InfoText = styled.p``;

const GameDescription = styled.div``;
