import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails, deleteGame } from "../redux/actions";
import { useEffect, useState } from "react";
import { useHistory, Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const history = useHistory(); 
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");

  


  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <MainDetail>
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <ContainerDetail>
            <Title>{myVideogame.name}</Title>
            <GameAbout>
              <H2About>Descripción</H2About>
              <About>
                <p
                  dangerouslySetInnerHTML={{ __html: myVideogame.description }}
                />
              </About>
            </GameAbout>
            <InfoDetail>
              <div>
                <InfoDetailH2>Plataformas: </InfoDetailH2>
                <InfoDetailP1>
                  {detailPlatform ? detailPlatform : "No tiene plataformas"}
                </InfoDetailP1>
              </div>
              <div>
                <InfoDetailH2>Generos: </InfoDetailH2>
                <InfoDetailP1>{detailGenres}</InfoDetailP1>
              </div>
              <div>
                <InfoDetailH2>Lanzamiento: </InfoDetailH2>
                <InfoDetailP2>{myVideogame.released}</InfoDetailP2>
              </div>
              <div>
                <InfoDetailH2>Rating: </InfoDetailH2>
                <InfoDetailP2>{myVideogame.rating}</InfoDetailP2>
              </div>
              <br />
            </InfoDetail>
            <Link to="/home">
              <Button>⇐ Volver</Button>
              {!Number(id) && (
              <ButtonEliminar >Eliminar</ButtonEliminar>)}
            </Link>
          </ContainerDetail>
          <ImageDetail src={myVideogame.image} alt="img not found" />
        </div>
      ) : (
        <Loading />
        
      )}
    </MainDetail>
  );
}

const ButtonEliminar = styled.div `
`

const MainDetail = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  background-color: #151515;
`;

const ContainerDetail = styled.div`
  color: rgb(237, 237, 237);
  padding-top: 120px;
  width: 100%;
  height: 820px;
  position: absolute;
  z-index: 1;
  background: linear-gradient(90deg, #000000, #00000000);
`;

const Title = styled.div`
  font-size: 85px;
  line-height: 70px;
  font-weight: 800;
  text-shadow: -3px 3px black;
  margin-left: 10rem;
  margin-right: 10rem;
  margin-top: 1rem;
`;

const GameAbout = styled.div`
  margin-top: 103px;
  margin-left: 30px;
`;

const H2About = styled.h2`
  color: #ffc300;
  text-shadow: 1px 1px #000000;
  margin: 0 0 8px;
  margin-left: -80rem;
`;

const About = styled.div`
  display: flex;
  width: 500px;
  height: 320px;
  line-height: 22px;
  overflow-y: scroll;
  text-align: left;
  margin-left: 15rem;
`;

const InfoDetail = styled.div`
  display: flex;
  margin: 25px 4px 4px 20px;
  position: absolute;
  top: 13.7rem;
  left: 52rem;
`;

const InfoDetailH2 = styled.h2`
  margin-top: 7rem;
  margin-left: 70px;
  display: flex;
  justify-content: display-start;
  color: #ffc300;
  text-shadow: 1px 1px #000000;
`;

const InfoDetailP1 = styled.p`
  max-width: 10px;
  margin-bottom: 8px;
  margin-left: 70px;
`;

const InfoDetailP2 = styled.p`
margin-left: 70px
`;

const Button = styled.button`
  width: 200px;
  height: 50px;
  font-size: 1.5rem;
  color: black;
  outline: 0;
  border: 0;
  border-radius: 8px;
  background-color: #ffc300;
  border: solid 1px black;
  cursor: pointer;
  margin-left: 70rem;
  margin-top: -10rem;
  

  &:hover {
    background-color: black;
    color: #ffc300;
    transition: all 0.2s ease;
  }
`;
const ImageDetail = styled.img`
position: absolute;
width: 100%;
height: 100%;
object-fit: cover;
filter: blur(4px);
}
`;
