import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

function Card({
  id,
  name,
  description,
  platforms,
  image,
  released,
  rating,
  genres,
  background_image,
}) {
  return (
    <div id="card">
      <h3>{name}</h3>
      <img
        src={image ? image : background_image}
        alt="Img not found"
        width="300px"
        height="200px"
      />
      <h5>Género: {genres?.join(", ")}</h5>
      <h5> Rating: {rating}</h5>
        <Link to={`/details/${id}`}>
            <button id="MoreInfo">Más información</button>
        </Link>
    </div>
  );
}

const Name = styled.h3`
color: green 
`

export default Card;
