import React, {Fragment} from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
    getVideogames,
    filterByGenres,
    getGenres,
    filterCreated,
    orderByRating,
    sortAlphabetically,
    orderByName,
    sortByRating,
} from "../actions/index"
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";
import Loading from "./Loading";
import NotFound from "./NotFound";
//IMPORTAR ESTILOS

export default function Home() {
  const dispatch = useDispatch();
  const allVideogames = useSelector((state) => state.videogames);
  const allgenres = useSelector((state) => state.allgenres);
  const [loading, setLoading] = useState(true);
  const [header, setHeader] = useState("Explore all the Games");
  const [order, setOrder] = useState("");
  const [setOrdenRating] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [videogamePerPage] = useState(15);
  const indexOfLastVideogame = currentPage * videogamePerPage;
  const indexOfFirtsVideogame = indexOfLastVideogame - videogamePerPage;
  const currentVideogame = allVideogames.slice(
    indexOfFirtsVideogame,
    indexOfLastVideogame
  );

  useEffect(() => {
    if(allVideogames.length > 0 ){
      setLoading(!loading)
    }
  }, [allVideogames, loading]) 

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  if (allVideogames.length > 0 && loading) {
    setLoading(false);
  }

  useEffect(() => {
    console.log("algo")
    dispatch(getVideogames());
    dispatch(getGenres());
  }, [dispatch]);

  function handleClick(element) {
    element.preventDefault();
    dispatch(getVideogames());
  }

  function handlePrev(element) {
    element.preventDefault();
    setCurrentPage(currentPage - 1);
  }

  function handleNext(element) {
    element.preventDefault();
    setCurrentPage(currentPage + 1);
  }

  function handleFilterGenres(element) {
    dispatch(filterByGenres(element.target.value));
    setCurrentPage(1);
  }
  function handleFilterCreated(element) {
    element.preventDefault();
    dispatch(filterCreated(element.target.value)); //PARA CREAR UN NUEVO JUEGO
  }
  function handleRatingSort(e) {
    e.preventDefault();
    dispatch(sortByRating(e.target.value));
    setCurrentPage(1);
    setOrder(`Sorted by: ${e.target.value}`);
    setHeader(`Sorted by: ${e.target.value}`);
  }
//ERROR EN CONSOLA A VERIFICAR
  function handleSort(element) {
    element.preventDefault();
    dispatch(sortAlphabetically(element.target.value));
    setCurrentPage(1);
    setOrder(`Sorted by: ${element.target.value}`);
    setHeader(`Sorted by: ${element.target.value}`);
    return(
      <div className="background-image">
      <div className="home">
          <button className="clear-button" onClick={(e) => refresh(e)}>Clear Filters</button>
          {/* <button className="home-button" onClick={() => window.location.reload(false)}>Clear Filters</button> */}
          
          <SearchBar />
          <Filters />
          
              
              <Paginado 
                  videogamesPerPage={videogamesPerPage}
                  allVideogames={allVideogames.length}
                  pages={pages}
              />
              

              {allVideogames.length == 0 && allVideogames  ? (<Loading />) :
              <div className="container">
              {
                   Array.isArray(currentVideogame) ? currentVideogame?.map(e => {
                      return (
                          <div className="card">
                      <Fragment>
                           <Link to={`/videogame/${e.id}`} className="card-link" style={{ textDecoration: 'none' }} > 
                           <Card 
                              key={e.id} 
                              name={e.name}
                              genres={e.genres.map(v => v).join(", ")}
                              background_image={e.background_image}
                              id={e.id}
                              rating={e.rating}
                           />
                          </Link>
                       </Fragment>
                       </div>
                  )}) : (<div className="name-not-found"><h1>Videogame not found, please try another search</h1></div>)
              } 
              </div>
              }

      </div> 
      </div>
  )
      }
}
