import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "./Loading";
import styled from "styled-components";

export default function Details() {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetails(id));
  }, [dispatch, id]);

  const myVideogame = useSelector((state) => state.detail);
  const detailPlatform = myVideogame.platforms?.join(", ");
  const detailGenres = myVideogame.genres?.join(", ");
  const backgroundUrl = myVideogame.backgroundImage;

  if (Object.keys(myVideogame).length > 0 && loading) {
    setLoading(false);
  }

  return (
    <DetailContainer style={{}}>
      {Object.keys(myVideogame).length > 0 && !loading ? (
        <div>
          <GameImage src={myVideogame.image} alt="img not found" />
          <GameTitle>{myVideogame.name}</GameTitle>
          <InfoContainer>
            <InfoLabel>Plataformas:</InfoLabel>
            <InfoText>
              {detailPlatform ? detailPlatform : "No tiene plataformas"}
            </InfoText>
          </InfoContainer>
          <InfoContainer>
            <InfoLabel>Generos:</InfoLabel>
            <InfoText>{detailGenres}</InfoText>
          </InfoContainer>
          <InfoContainer>
            <InfoLabel>Lanzamiento:</InfoLabel>
            <InfoText>{myVideogame.released}</InfoText>
          </InfoContainer>
          <InfoContainer>
            <InfoLabel>Rating:</InfoLabel>
            <InfoText>{myVideogame.rating}</InfoText>
          </InfoContainer>
          <GameDescription>
            <p>{myVideogame.description}</p>
          </GameDescription>
          <Link to="/home">
            <Button>Volver</Button>
          </Link>
        </div>
      ) : (
        <Loading />
      )}
    </DetailContainer>
  );
}

const DetailContainer = styled.div``;

const GameImage = styled.img`
  width: 40rem;
  height: auto;
  margin-top: 10rem;
  margin-right: 45rem;
  border-radius: 2%;
  overflow: hidden;
  transition: 0.8s;

  &:hover {
    transform: scale(1.05);
    color: #fff;
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #fff9f9ea, 0 0 10px #fff9f9ea;
  }

  @media (max-width: 950px) {
    min-width: 20rem;
  }
`;

const GameTitle = styled.h2`
  margin-bottom: 3rem;
  margin-top: -24rem;
  margin-left: 35rem;
  font-size: 60px;
`;

const InfoContainer = styled.div`
  display: flex;
  margin-top: 1rem;
`;

const InfoLabel = styled.label`
  display: flex;
  font-weight: bold;
  width: 10rem;
  flex-direction: flex-start;
  margin-left: 60.5rem;
  margin-top: 0rem;
  font-size: 20px;
`;

const InfoText = styled.p`
  display: flex;
  justify-content: flex-end;
  margin-right: 30rem;
  margin-top: 0rem;
  font-size: 20px;
`;

const GameDescription = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 1.5rem;
  margin-left: 17.5rem;
  border: 2px solid #ffc300;
  padding: 01rem;
  max-width: 31.5%;
  text-align: left;
`;

const Button = styled.button`
  position: absolute;
  top: 51rem;
  right: 36rem;
  background-color: #ffc300;
  font-weight: bold;
  color: white;
  padding: 10px 4rem;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #555;
  }
`;
