const initialState = {
    videogames: [],
}

function rootReducer (state = initialState, action){
    switch(action.type) {
        case 'GET_VIDEOGAMES':
            return {
                ...state,
                videogames: action.payload
            }

        case 'FILTER_BY_GENRES':
            const allVideogames = state.originalVideogames;
            const genresFiltered = action.payload === 'All' ? allVideogames : allVideogames.filter(e => e.genres === action.payload)
            return {
                ...state,
                videogames: genresFiltered
            }
            default: 
            return state;
    }
}

export default rootReducer;
